INSERT INTO `dle_wtgames` VALUES 
('1', '1852', 'Шустрая змейка', 'shustraya_zmeika', 'Любимая всеми змейка появилась в новом образе. Съешь самое большее количество яблок.', '738', '710', 'vremya,zmeika,skorost,lovkost', 'arkady', 'http://static.apitech.ru/45AC7C729FCB4A4880ED3ECF251C54CERU', '0.01', '1', '1', '1438722000', 'png', 'jpg', 'jpg', 'jpg', 'jpg', 'jpg'),
('2', '1847', 'Мокрая киса', 'mokraya_kisa', 'Не дай котёнку промокнуть! ', '638', '710', 'arkady,koshki,kotiki,dozhd', 'arkady', 'http://static.apitech.ru/3C6480FC63A84033A4F2E2925C6195B9RU', '0.01', '1', '2', '1435093200', 'png', 'jpg', 'jpg', 'jpg', 'jpg', 'jpg'),
('3', '1843', 'Котоландия 2', 'catalance_2', 'Физический паззл. Продолжение саги о симпатичных котах.\nРасставляй котов, собирай звёзды и помоги им сделать семейную фотографию. Пройди 20 дополнительных уровней.', '668', '750', 'balans,zhivotnye,kotiki', 'logicheskie,fizicheskie_igry', 'http://static.apitech.ru/A6A9809D7F994C1DA7DBA8379EB489F4RU', '0.01', '1', '3', '1432846800', 'png', 'jpg', 'jpg', 'jpg', 'jpg', 'jpg'),
('4', '1841', 'Пяченька', 'spasi_pechenyukhu', 'Печенька - увлекательная игра , где необходимо крушить тараканов. Не пусти их съесть печеньку, побей рекорды друзей и отведай сладкого! Адреналин зашкаливает! ', '658', '740', 'arkady,nasekomye,lovkost', 'arkady', 'http://static.apitech.ru/372BD86224DE43538ABAAE942B70EBB0RU', '0.01', '1', '4', '1431464400', 'png', 'jpg', 'jpg', 'jpg', 'jpg', 'jpg'),
('5', '1837', 'Строительные кубики', 'building_cubes', 'Парень предложил девушке выйти за него замуж. Она сказала, да ... но где они будут жить? Парень указал место где он живет, это была лужайка в парке, представляете?!!!... он жил в парке. Девушка была в недоумении… она там жить не хочет. \nВам нужно построить дом, на это у вас есть 34 уровня. Если вы не сможете построить дом, то она уйдет жить с банкиром! Приятной игры!\n', '713', '810', 'kubiki,tri_v_ryad,stroitelstvo', 'arkady', 'http://static.apitech.ru/85FDC02F50BE45208FFEBBE500798DA0RU', '0.01', '1', '5', '1428526800', 'png', 'jpg', 'jpg', 'jpg', 'jpg', 'jpg'),
('6', '1831', 'Weesix', 'weesix', 'Помогите красной игрушечной машинке преодолеть препятствия в гонке с синим автомобилем, который, очевидно лучше.', '713', '810', 'logicheskie,mashiny,golovolomka', 'logicheskie,fizicheskie_igry,kvesty', 'http://static.apitech.ru/1196163818D540E4B2E5B1D5840FF00ERU', '0.01', '1', '6', '1427144400', 'png', 'jpg', 'jpg', 'jpg', 'jpg', 'jpg'),
('7', '1811', 'Государство', 'gosudarstvo', 'Соединяйте клетки! Получайте очки! Соберите \"Государство\"!', '588', '660', 'logicheskie,strategii,kubiki', 'logicheskie', 'http://static.apitech.ru/90C2D7A67BA24C5A93172BD8D0F9B401RU', '0.01', '1', '7', '1421182800', 'png', 'jpg', 'jpg', 'jpg', 'jpg', 'jpg'),
('8', '1804', 'Маленькие воришки', 'malenkie_vorishki', 'Помогите двум маленьким воришкам добраться до сыра и гамбургера. Но сделать  это будет не так то просто. Выбирайте маршрут движения, избегайте ловушек, помогайте друг другу, и тогда вы доберетесь до цели.', '668', '750', 'eda,smeshnye,kotiki', 'logicheskie', 'http://static.apitech.ru/CC333F900AE247D195DDC6CFF35827EBRU', '0.01', '1', '8', '1419454800', 'png', 'jpg', 'jpg', 'jpg', 'jpg', 'jpg'),
('9', '1794', 'Спринтер в Школе', 'sprinter_v_shkole', 'Спринтер в школе. Помогите человечку, нарисованному на доске, добежать как можно дальше и набрать как можно больше очков. Для этого собирайте буквы, таймеры, перепрыгивайте пропасти и зарабатывайте достижения.', '668', '750', 'prepyatstviya,skorost,shkola', 'arkady', 'http://static.apitech.ru/D1158E03B14842569A7A06D062874002RU', '0.01', '1', '9', '1412539200', 'png', 'jpg', 'jpg', 'jpg', 'jpg', 'jpg'),
('10', '1783', 'Собирай Урожай', 'sobirai_urozhai', 'Пришла осень, наступила пора собирать урожай. \nПомогите фермеру на тракторе собрать как можно больше урожая. Набирайте очки собирая овощи и канистры с топливом, и остерегайтесь зеленых сорняков! \nСоревнуйтесь с друзьями - кто соберет больше урожая и наберет больше очков?', '668', '750', 'vremya,mashiny,prepyatstviya', 'arkady', 'http://static.apitech.ru/404EDD8A8A924781BD3B349A4F03D5F4RU', '0.01', '1', '10', '1409515200', 'png', 'jpg', 'jpg', 'jpg', 'jpg', 'jpg'),
('11', '1780', 'Stickman sniper', 'stickman_sniper', 'Вам предстоит играть снайпером киллером.\nНеобходимо ликвидировать опасных преступников и террористов стикманов.\nРазные локации (улицы, парки и дворы) ждут вас.\nНужно стрелять по преступникам, при этом, не попав в обычных гражданских людей.\nУлицы будут полны гражданскими людьми стикманами и автотранспортом. \nНайдите вашу цель и уничтожьте ее.', '728', '1070', 'snaiper,vnimanie,skorost', 'arkady', 'http://static.apitech.ru/37927AAC26874D6DA3CA49D55FF2DA9DRU', '0.01', '1', '11', '1408392000', 'png', 'jpg', 'jpg', 'jpg', 'jpg', 'jpg'),
('12', '1777', 'Хитрый Рик 2', 'tricky_rick_2', 'Красочное продолжение первой части приключений Рика. На этот раз Рик потерпел крушение на неизвестной планете. Помоги ему собрать все энергетические шары, чтобы послать сигнал SOS и вызвать спасательный корабль!', '668', '750', 'vremya,roboty,lovkost', 'logicheskie,priklyucheniya', 'http://static.apitech.ru/CCC644D633004447A914C5AC9E416D88RU', '0.01', '1', '12', '1407268800', 'png', 'jpg', 'jpg', 'jpg', 'jpg', 'jpg'),
('13', '1772', 'Ведро охранник', 'bucket_cop', 'Вы ведро. Вы охранник в тюрьме. Но вдруг, все преступники сбежали из своих камер, и вы остались один на один с ними. Ваша задача поймать их всех, и вернуть на место. В игре много логических задач, 19 преступников. Чем дальше вы будете играть, тем сложнее будут враги.', '783', '654', 'logicheskie,lovkost', 'fizicheskie_igry', 'http://static.apitech.ru/326E033ED0A94C038E3A856F269CBE91RU', '0.01', '1', '13', '1414616400', 'png', 'jpg', 'jpg', 'jpg', 'jpg', 'jpg'),
('14', '1770', 'Сапёр', 'sapper', 'Злой доктор TNT украл с неба все звезды, и заминировал их. Ваша задача разминировать  механизм, и спасти звездочки. Будьте осторожны, доктор TNT очень хитёр и опасен. ', '713', '810', 'vremya,logicheskie,golovolomka,vzryv,dinamit', 'logicheskie,fizicheskie_igry', 'http://static.apitech.ru/2B35D4A0DC984771B0BFCE190ABE052ARU', '0.01', '1', '14', '1408392000', 'png', 'jpg', 'jpg', 'jpg', 'jpg', 'jpg'),
('15', '1769', 'Снайпер', 'snaiper11', 'Вы попали в заброшенный город полный монстров, но у вас есть снайперская винтовка. Ваша задача находить и убивать монстров.', '668', '750', 'monstry,snaiper,uzhastiki', 'arkady', 'http://static.apitech.ru/1384CB08DA2F466F96D4E1DB0CEDBD01RU', '0.01', '1', '15', '1404676800', 'png', 'jpg', 'jpg', 'jpg', 'jpg', 'jpg'),
('16', '1766', 'Сноуборд Выживание', 'snoubord_vyzhivanie', 'Мини сноуборд игра. Установи рекорд дальности.', '668', '750', 'vremya,sneg,lovkost', 'arkady', 'http://static.apitech.ru/939199E8F8684D2A8E6D193C00F1ECFCRU', '0.01', '1', '16', '1403812800', 'png', 'jpg', 'jpg', 'jpg', 'jpg', 'jpg'),
('17', '1765', 'Коты Выживание', 'koty_vyzhivanie', 'Мини игра, помоги мышке выжить в комнате с котами.', '668', '750', 'reaktsiya,lovkost,kotiki', 'arkady', 'http://static.apitech.ru/5AAADBC662654BCFB0BE337102CB4421RU', '0.01', '1', '17', '1404072000', 'png', 'jpg', 'jpg', 'jpg', 'jpg', 'jpg'),
('18', '1758', 'Заброшенный дом', 'zabroshennyi_dom', 'Вам предстоит найти в мрачном, заброшенном доме свою подругу. Детский смех и скрежет когтей монстров в темноте не дадут вам расслабиться. Холодный пот будет выступать на вашей коже от леденящего страха и ужаса. Заброшенный дом окажется не таким заброшенным. Множество призраков, приведений, монстров, зомби, мутантов, ходячих трупов будут пугать вас до смерти. Вам уже не сможет помочь психиатр, ведь демоны будут в вашей голове.', '728', '1070', 'monstry,prizraki,uzhastiki', 'poisk_predmetov,priklyucheniya,brodilki', 'http://static.apitech.ru/1F79198D0D63478EB2514409D068D8E3RU', '0.01', '1', '18', '1403121600', 'png', 'jpg', 'jpg', 'jpg', 'jpg', 'jpg'),
('19', '1756', 'Стальной Джэк', 'steel_jack', 'Пробеги по следу украденных сокровищ, управляя отважным рыцарем, и найди похищенного короля и принцессу!', '668', '750', 'rytsari,reaktsiya,lovkost', 'arkady,fizicheskie_igry', 'http://static.apitech.ru/A1F4C19DEB664F9596D6CF63FFEF351CRU', '0.01', '1', '19', '1402948800', 'png', 'jpg', 'jpg', 'jpg', 'jpg', 'jpg'),
('20', '1755', 'Желе Боб', 'jally_bob', 'Желе Боб - пришелец из далеких планет ищет всевозможные фрукты. Помоги Бобу собрать фрукты и узнай для чего инопланетянам нужно так много фруктов.', '668', '750', 'frukty,inoplyanetyane,ravnovesie', 'logicheskie', 'http://static.apitech.ru/D89A0586788647AD804270C0AC346C28RU', '0.01', '1', '20', '1403035200', 'png', 'jpg', 'jpg', 'jpg', 'jpg', 'jpg'),
('21', '1754', 'Хитрый Рик', 'tricky_rick', 'Помоги Рику собрать все украденные канистры с топливом! Используй молот, бомбы, реактивный ранец и другие полезные предметы для решения головоломок!\n\n', '668', '750', 'vremya,roboty,lovkost', 'logicheskie,priklyucheniya', 'http://static.apitech.ru/FC898236DB06473A8DB336D5473F1FF6RU', '0.01', '1', '21', '1403035200', 'png', 'jpg', 'jpg', 'jpg', 'jpg', 'jpg'),
('22', '1753', 'Клоун Пуки', 'ragdoll_clown', 'Динамичный физический платформер с оригинальной концепцией, в котором вам предстоит управляя клоуном, пролететь два десятка уникальных лабиринтов и заработать награды!', '668', '750', 'arkady,smeshnye,kloun', 'arkady,fizicheskie_igry', 'http://static.apitech.ru/7A78D1B9372546E19726BC63CA624D7ARU', '0.01', '1', '22', '1402948800', 'png', 'jpg', 'jpg', 'jpg', 'jpg', 'jpg'),
('23', '1752', 'Безголовый Джо', 'headless_joe', 'Помогите Джо вернуть свою голову! Проведите его по всем игровым уровням, решите все головоломки и соберите все болты, чтобы Джо смог крепко прикрутить свою голову на место!', '668', '750', 'vremya,logicheskie,roboty,golovolomka', 'logicheskie,fizicheskie_igry', 'http://static.apitech.ru/426122E2C1974A378B569AB0AA6EC6DBRU', '0.01', '1', '23', '1401998400', 'png', 'jpg', 'jpg', 'jpg', 'jpg', 'jpg'),
('24', '1750', 'Маджонг : Забавная Мексика', 'madzhong__zabavnaya_meksika', 'Получите больше удовольствия с этой уникальной игрой-головоломкой Маджонг Забавная Мексика! Выберите совпадающие пары плиток, избавляйтесь от них на доске головоломки маджонг. Только открытые плитки могут быть уничтожены, ваша задача - убрать все кости с игровой доски. Но Маджонг - это не просто примитивная игра, где от Вас требуется переставлять только кости. Вы должны еще проводить расчеты и разрабатывать стратегию. Ну, и конечно же, здесь не обойтись без везения.', '764', '865', 'vremya,logicheskie,naiti,nastolnye,razvlecheniya', 'logicheskie,kvesty', 'http://static.apitech.ru/6BA6894BCEB345F0AD600650BE58243ERU', '0.01', '1', '24', '1401998400', 'png', 'jpg', 'jpg', 'jpg', 'jpg', 'jpg'),
('25', '1749', 'Быки Выживание', 'byki_vyzhivanie', 'Сколько ты продержишься на арене с быками?', '668', '750', 'zhivotnye,spasatelnye,skorost,lovkost', 'arkady', 'http://static.apitech.ru/66A6CE6130D8425DBC36E636B592A2FBRU', '0.01', '1', '25', '1401998400', 'png', 'jpg', 'jpg', 'jpg', 'jpg', 'jpg'),
('26', '1739', 'Космический полёт', 'kosmicheskii_polyot', 'Вы отправились в космический полёт на своём летающем корабле, и неожиданно начался метеоритный дождь. Постарайтесь отбиться от метеоритов и выжить!', '888', '810', 'kosmicheskie,letalki,prepyatstviya', 'arkady', 'http://static.apitech.ru/5AB52C0D9A114FB48F581D35DA6335F2RU', '0.01', '1', '26', '1401134400', 'png', 'jpg', 'jpg', 'jpg', 'jpg', 'jpg'),
('27', '1738', 'Пожиратель морковки', 'pozhiratel_morkovki2', 'Помоги голодному зайчику набить брюхо, но остерегайся шишек.', '688', '660', 'kroliki,reaktsiya,skorost,morkovka', 'arkady', 'http://static.apitech.ru/77750E0461BC48E78469ECD5E22697C9RU', '0.01', '1', '27', '1400443200', 'png', 'jpg', 'jpg', 'jpg', 'jpg', 'jpg'),
('28', '1737', ' Виктория Вейн: Картина проклятия', 'viktoriya_vein_kartina_proklyatiya', 'Доставшаяся в наследство от бабушки старинная картина оказывается совсем непростой. Именно она приводит Викторию Вейн на таинственный остров, которым правит злобная ведьма. Она заколдовала всех жителей острова и требует отдать картину ей. Какая же сила в ней таится, и почему она так важна для ведьмы? Исследуйте живописные уголки острова, решайте головоломки, ищите скрытые предметы, чтобы спасти жителей острова и узнать историю, связанную с проклятой картиной.', '764', '865', 'vremya,naiti,vnimanie,taina', 'poisk_predmetov,priklyucheniya', 'http://static.apitech.ru/CBEBB4D15AEF4D11B643A5C12360A46ARU', '0.01', '1', '28', '1400184000', 'png', 'jpg', 'jpg', 'jpg', 'jpg', 'jpg'),
('29', '1736', 'Поиск предметов : Таинственный дом сокровищ', 'poisk_predmetov__tainstvennyi_dom_sokrovisch', 'При исследовании ваших семейных архивов вы обнаружили, что вы унаследовали Таинственный дом с сокровищами! Теперь вы и ваш брат агент по недвижимости, собрались разыскать сокровища. Но прежде вам нужно добраться до таинственного дома полного опасностей. Тем не менее, что-то не так в этом старом доме. Вскоре вы обнаружите, что наследование не дается легко, в этой захватывающей игре скрытых объектов! Сможете ли вы пережить ночь и раскрыть секрет Вашего предка и найти сокровища, которые скрыты в Таинственном доме?', '764', '865', 'vremya,naiti,vnimanie,taina', 'poisk_predmetov,priklyucheniya', 'http://static.apitech.ru/02B008F7BFAE45928F02252B96F31F8BRU', '0.01', '1', '29', '1400616000', 'png', 'jpg', 'jpg', 'jpg', 'jpg', 'jpg'),
('30', '1735', ' Тайная Экспедиция : Поиск золотого черепа', 'tainaya_ekspeditsiya__poisk_zolotogo_cherepa', 'Вы находитесь в неизвестном для вас месте, одно точно понятно, что это поезд. Банда опасных воров требуют от вас информации о Золотом Черепе. После долгих и мучительных пыток вы говорите им, где найти золотой череп. У вас есть время, чтобы найти осколки черепа прежде чем они сделают это до вас. В этой захватывающей серии Скрытые экспедиции, ваше путешествие приведет вас из замка до края джунглей, от мельчайших рек до океанов. Как вам отследить недостающие осколки и узнать секреты Золотого Черепа? Это и является вашей целью.', '764', '865', 'vremya,naiti,vnimanie,taina', 'poisk_predmetov,priklyucheniya', 'http://static.apitech.ru/266D1C24421041F8A335F65BDDC82E8BRU', '0.01', '1', '30', '1400529600', 'png', 'jpg', 'jpg', 'jpg', 'jpg', 'jpg'),
('31', '1734', ' Детективное агенство : Преступление лорда', 'detektivnoe_agenstvo__prestuplenie_lorda', 'Спаси себя и человечество! Это произошло в 1960-х годах в Англии. Один красивый особняк в тени ветвистых деревьев. Вы путешествовали в течение нескольких дней, чтобы достичь этот замок, скрытый в глубине. С дружественным помещиком и недорогой арендой, большинство людей будут завидовать тому, кто живет здесь. Но кровавые вещи происходят внутри этого дома. Есть ли кто-нибудь, кто сможет предотвратить этот кошмар?', '764', '865', 'vremya,naiti,vnimanie,taina,sokrovischa', 'poisk_predmetov', 'http://static.apitech.ru/01B0B657748C4705857D1175D9E7E098RU', '0.01', '1', '31', '1402430400', 'png', 'jpg', 'jpg', 'jpg', 'jpg', 'jpg'),
('32', '1733', 'Детективное агенство 3: Проклятое зеркало', 'detektivnoe_agenstvo_3_proklyatoe_zerkalo', 'Веселая и одновременно мистическая новая игра скрытых объектов. Откройте для себя более 10 уровней скрытых объектов с веселой сюжетной линией и уникальными мини-играми. Взаимодействуйте с пунктами, чтобы раскрыть тайну осколков зеркала, чтобы восстановить и избежать проклятия зеркала.', '764', '865', 'vremya,naiti,vnimanie,taina,sokrovischa', 'poisk_predmetov', 'http://static.apitech.ru/0742F676958F45FBB4743AB816BDBC7BRU', '0.01', '1', '32', '1402862400', 'png', 'jpg', 'jpg', 'jpg', 'jpg', 'jpg'),
('33', '1732', 'Детективное агенство 2: Аномальный феномен', 'detektivnoe_agenstvo_2_anomalnyi_fenomen', 'Сохраните души детей-вундеркиндов и их воспитателя, пропавших во время пожара в Агентстве аномалий! В качестве агента Агентства аномалий, вас отправили в командировку, чтобы найти фантомы и выяснить, кто начал смертельную гонку за душами детей. Помогите жителям приюта использовать свои необычные способности и нарушить планы мистического братства и бежать! Исследуйте великолепные скрытые сцены объектов, чтобы решить паранормальные тайны!', '764', '865', 'vremya,naiti,vnimanie,taina,sokrovischa', 'poisk_predmetov', 'http://static.apitech.ru/28E21AC5F0B3445C8B6894ED162BC1DBRU', '0.01', '1', '33', '1401912000', 'png', 'jpg', 'jpg', 'jpg', 'jpg', 'jpg'),
('34', '1731', 'Древние сокровища : Тайна Фараона', 'drevnie_sokrovischa__taina_faraona', 'Как оператор и куратор музея, вам необходимо перейти по следу древних культур, чтобы найти утраченные артефакты, чтобы заполнить недостающую коллекцию. Используйте свои навыки и амбиции, чтобы собрать все самое лучшее, чтобы найти все скрытые объекты, в этой сложной игре на протяжении всего пути. Как только вы найдёте сокровища, они будут поражать и радовать всех посетителей. Это может показаться легким на первый взгляд, но сложным в освоении.', '764', '865', 'vremya,naiti,vnimanie,taina,sokrovischa', 'poisk_predmetov', 'http://static.apitech.ru/62E80AA833704E759E35C8A8226883C7RU', '0.01', '1', '34', '1401825600', 'png', 'jpg', 'jpg', 'jpg', 'jpg', 'jpg'),
('35', '1730', 'Поиск предметов : Сокровища дяди Сэма', 'poisk_predmetov_sokrovischa_dyadi_sema', 'Откройте для себя скрытые сокровища в давно забытых руинах, борясь со злом дяди Сэма и его безумные дружков! У вас есть что нужно, чтобы успешно пройти через опасные ловушки и запутанные головоломки, которые будут препятствовать вашим прохождению? Выясните, в Скрытые Объекты: Сокровища дяди Сэма и мира полного опасностей, в невероятной игре скрытых объектов.', '764', '865', 'vremya,naiti,vnimanie,taina', 'poisk_predmetov,priklyucheniya', 'http://static.apitech.ru/0B007F07E58345449C74700B7AFFD228RU', '0.01', '1', '35', '1400011200', 'png', 'jpg', 'jpg', 'jpg', 'jpg', 'jpg'),
('36', '1724', 'Скрытые экспедиции: Детектив в порту Ниццы', 'detektiv_v_portu_nitstsy', 'Решите тайну приморского города и раскройте потерянные воспоминания в Скрытые экспедиции: Детектив порта Ниццы! После пробуждения на пустынном пляже без памяти, вы не можете вспомнить кто вы есть, вы отправились на поиски одного человека, который может помочь вам. Но когда вы приезжаете в ближайший приморский город, вы понимаете, что потеря памяти не является вашей единственной проблемой. Морская деревня гудит с сообщениями о страшных событий. Задержите дыхание и окунитесь в эту захватывающую игру скрытых объектов!', '764', '865', 'vremya,naiti,vnimanie,taina', 'poisk_predmetov,priklyucheniya', 'http://static.apitech.ru/0072D63CB875433A98089541187CBA35RU', '0.01', '1', '36', '1400011200', 'png', 'jpg', 'jpg', 'jpg', 'jpg', 'jpg'),
('37', '1717', 'Нелепая птица', 'nelepaya_ptitsa', 'Нелепая птица-лидер установок в AppStore! Соревнуйся с друзьями, набери как можно больше очков.', '668', '750', 'prepyatstviya,ptitsy,lovkost', 'arkady', 'http://static.apitech.ru/D4DA3FA87E764342B9DD64B4AEDB6665RU', '0.01', '1', '37', '1398715200', 'png', 'jpg', 'jpg', 'jpg', 'jpg', 'jpg'),
('38', '1716', 'Что на картинке 2', 'chto_na_kartinke_2', 'Вторая часть для любителей отгадывать картинки. Вас ждут новые картинки с различной тематикой.\nВам необходимо разгадать 100 картинок без единой подсказки.', '1021', '580', 'logicheskie,vnimanie,na_soobrazitelnost,znanie,kartinki,um', 'logicheskie', 'http://static.apitech.ru/8ACB075942114F1F84C1894259D88E3BRU', '0.01', '1', '38', '1398628800', 'png', 'jpg', 'jpg', 'jpg', 'jpg', 'jpg'),
('39', '1713', 'Шустрая Курица', 'shustraya_kuritsa', 'В твоей тетради завелась курочка. Помоги ей собрать всех своих цыплят и при этом набери как можно больше очков.\nЧтобы набрать очки, собирай своих цыплят, зеленый перец и таймеры. Остерегайся острых шипов!', '668', '750', 'vremya,devochkam,kuritsa,prepyatstviya,skorost', 'arkady', 'http://static.apitech.ru/179078DC56C4408E8FB6CF395E8B15C6RU', '0.01', '1', '39', '1398369600', 'png', 'jpg', 'jpg', 'jpg', 'jpg', 'jpg'),
('40', '1706', 'Думка', 'dumka', 'Новая версия игры DOOMka. Теперь она называется просто Думка.\nИгра выполнена в страшной атмосфере и с черным юмором. Любителям трэша и психоделики понравится эта игра.\nИгра похожа на квест с элементами экшн. В каждой локации нужно драться или разгадывать головоломки. Нужно будет найти способы открыть двери, убить монстров.', '728', '1070', 'monstry,prizraki,uzhastiki', 'poisk_predmetov,brodilki', 'http://static.apitech.ru/9738EA60320A45E8B716FF82AF1A9A72RU', '0.01', '1', '40', '1397592000', 'png', 'jpg', 'jpg', 'jpg', 'jpg', 'jpg'),
('41', '1695', 'Робомания', 'robomaniya', 'Соревнуйся с друзьями в увлекательной игре! Помоги роботу собрать недостающие части. Собирай шестеренки и гаечные ключи, и остерегайся другого мусора! Набери как можно больше очков за 2 минуты.', '668', '750', 'vremya,roboty,lovkost', 'arkady', 'http://static.apitech.ru/16819B1DCF7F4DC79C87FC7A2C3949BDRU', '0.01', '1', '41', '1396382400', 'png', 'jpg', 'jpg', 'jpg', 'jpg', 'jpg'),
